/* funcionalidades do menu */
$('.open-menu').click(function() {
	if ($('.menu').width() == 0) {
		$('.menu').show();
		$('.menu').animate({
			width: '50%'
		});
	} else {
		$('.menu').animate({
			width: '0'
		});
		$('.menu').hide(0);
	}
});

/* drop dos conte√∫dos do menu */
$('.menu-titulo').click(function() {
	$('.item-menu-filho').toggle();
});

/* funcionalidades do rodape */
$('.ir-topo').click(function() {
	$('html, body').animate({
		scrollTop: '0'
	}, 'medium');
});